    private void btnIniciarSesionActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIniciarSesionActionPerformed
        String correoElectronico = tfCorreoElectronico.getText();
        String contrasenia = new String(pfContrasenia.getPassword());
        InicioSesionControlador inicioSesionControlador = new InicioSesionControlador();
        
        try {
            inicioSesionControlador.obtenerConexion();
            inicioSesionControlador.validarDatos(correoElectronico, contrasenia);
            inicioSesionControlador.obtenerUsuario();
            inicioSesionControlador.validarRespuesta();
            
            new App().setVisible(true);
            dispose();
        } catch (SQLException | ClassNotFoundException ex) {
            JOptionPane.showMessageDialog(this, ex.getMessage(), "Error", JOptionPane.ERROR_MESSAGE);
        }catch (Exception ex) {
            JOptionPane.showMessageDialog(this, ex.getMessage(), "Precauci√≥n", JOptionPane.WARNING_MESSAGE);
        }
    }//GEN-LAST:event_btnIniciarSesionActionPerformed

